query:
query getTasksById($taskIds: [String]!, $groupId: String!) {
  tasksById(taskIds: $taskIds, groupId: $groupId) {
    ...Task
    __typename
  }
}

fragment Task on Task {
  _id
  groupId
  taskType
  streamIds
  eventInfo {
    eventId
    title
    clone
    __typename
  }
  seededEventIds
  private
  assigneeId
  createdBy
  integration {
    ... on TaskAsanaIntegration {
      service
      identifier {
        id
        url
        __typename
      }
      __typename
    }
    ... on TaskTrelloIntegration {
      service
      identifier {
        id
        url
        __typename
      }
      __typename
    }
    ... on TaskJiraIntegration {
      service
      identifier {
        id
        cloudId
        accountId
        url
        __typename
      }
      __typename
    }
    ... on TaskGithubIntegration {
      service
      identifier {
        id
        repositoryOwnerLogin
        repositoryName
        number
        type
        url
        __typename
      }
      __typename
    }
    ... on TaskTodoistIntegration {
      service
      identifier {
        id
        url
        __typename
      }
      __typename
    }
    ... on TaskGoogleCalendarIntegration {
      service
      identifier {
        sunsamaId
        __typename
      }
      __typename
    }
    ... on TaskOutlookCalendarIntegration {
      service
      identifier {
        sunsamaId
        __typename
      }
      __typename
    }
    ... on TaskGmailIntegration {
      service
      identifier {
        id
        messageId
        accountId
        url
        __typename
      }
      __typename
    }
    ... on TaskOutlookIntegration {
      service
      identifier {
        id
        internetMessageId
        conversationId
        accountId
        url
        __typename
      }
      __typename
    }
    ... on TaskSlackIntegration {
      service
      identifier {
        permalink
        __typename
      }
      __typename
    }
    ... on TaskNotionIntegration {
      service
      identifier {
        id
        workspaceId
        url
        __typename
      }
      __typename
    }
    ... on TaskClickUpIntegration {
      service
      identifier {
        id
        userId
        teamId
        url
        __typename
      }
      __typename
    }
    ... on TaskGitlabIntegration {
      service
      identifier {
        id
        __typename
      }
      __typename
    }
    ... on TaskEmailIntegration {
      service
      identifier {
        id
        __typename
      }
      content {
        subject
        text
        html
        from {
          name
          email
          __typename
        }
        date
        __typename
      }
      __typename
    }
    __typename
  }
  deleted
  text
  notes
  notesMarkdown
  notesChecksum
  editorVersion
  collabSnapshot
  completed
  completedBy
  completeDate
  completeOn
  duration
  runDate {
    startDate
    endDate
    __typename
  }
  snooze {
    userId
    date
    until
    __typename
  }
  dueDate
  comments {
    userId
    text
    markdown
    editorVersion
    groupId
    createdAt
    editedAt
    deleted
    file
    fileMetadata {
      url
      filename
      mimetype
      size
      width
      height
      __typename
    }
    __typename
  }
  orderings {
    ordinal
    panelDate
    channelId
    userId
    __typename
  }
  subtasks {
    _id
    title
    completedDate
    completedBy
    timeEstimate
    actualTime {
      ...TaskActualTime
      __typename
    }
    __typename
  }
  subtasksCollapsed
  sequence {
    date
    id
    expiresDate
    ruleString
    searchable
    forked
    final
    estimatedStart {
      hour
      minute
      __typename
    }
    __typename
  }
  followers
  timeEstimate
  actualTime {
    ...TaskActualTime
    __typename
  }
  createdAt
  lastModified
  objectiveId
  __typename
}

fragment TaskActualTime on TaskActualTime {
  userId
  startDate
  endDate
  duration
  isTimerEntry
  __typename
}
